INSERT INTO `law_data_total` VALUES (49, '2018年1月6日凌晨4时许,被告人冼某未依法取得机动车驾驶证、醉酒后驾驶无号牌正三轮摩托车,途经中山市五桂山办事处长命水石榴杭上街下巷9号对开路段时,碰撞被害人李某停放在该处的粤T×××××号小轿车而肇事。肇事后,冼某报警并在现场等候处理,随后被公安人员抓获。经广东岐江司法鉴定所鉴定,冼某驾驶车辆时血液中乙醇含量为214.4mg/100ml。经公安交警部门认定,冼某承担事故的全部责任。', '危险驾驶', '李某', '冼某', '\n{\n  \"时间\": \"2018年1月6日凌晨4时许\",\n  \"地点\": \"中山市五桂山办事处长命水石榴杭上街下巷9号对开路段\",\n  \"人物\": \"被告人冼某、被害人李某\",\n  \"事件\": [\n    \"冼某未依法取得机动车驾驶证、醉酒后驾驶无号牌正三轮摩托车\",\n    \"在上述地点碰撞李某停放的粤T×××××号小轿车而肇事\",\n    \"冼某报警并在现场等候处理\",\n    \"随后被公安人员抓获\",\n    \"经司法鉴定，冼某驾驶车辆时血液中乙醇含量为214.4mg/100ml\",\n    \"公安交警部门认定，冼某承担事故的全部责任\"\n  ]\n}\n', '\n{\n  \"不利信息\": [\n    \"冼某未依法取得机动车驾驶证\",\n    \"醉酒后驾驶无号牌正三轮摩托车\",\n    \"碰撞李某停放的车辆而肇事\",\n    \"经司法鉴定，冼某驾驶车辆时血液中乙醇含量为214.4mg/100ml\",\n    \"公安交警部门认定，冼某承担事故的全部责任\"\n  ],\n  \"有利信息\": [\n    \"冼某报警并在现场等候处理\"\n  ],\n  \"中立信息\": [\n    \"事件发生时间为2018年1月6日凌晨4时许\",\n    \"地点为中山市五桂山办事处长命水石榴杭上街下巷9号对开路段\",\n    \"冼某随后被公安人员抓获\",\n    \"无其他提及的有利或不利的个人信息或行为\"\n  ]\n}\n', '1.冼某未依法取得机动车驾驶证\n2.醉酒后驾驶无号牌正三轮摩托车\n3.碰撞李某停放的车辆而肇事\n4.经司法鉴定，冼某驾驶车辆时血液中乙醇含量为214.4mg/100ml\n5.公安交警部门认定，冼某承担事故的全部责任\n', '1.冼某报警并在现场等候处理\n', '1.事件发生时间为2018年1月6日凌晨4时许\n2.地点为中山市五桂山办事处长命水石榴杭上街下巷9号对开路段\n3.冼某随后被公安人员抓获\n4.无其他提及的有利或不利的个人信息或行为\n');
