INSERT INTO `law_data_total` VALUES (693, '2014年10月初，被告人刘某和郭某甲、郭某乙、万某某合谋实施××。郭某甲、郭某乙冒充武警部队工作人员拨打电话，谎称部队需购买产品，并希望对方代购“自热米饭”。刘某告知对方其经销合同已到期，让对方联系生厂商，并告知万某某电话。万某某要求先付款再发货，告知对方银行卡账号。2014年10月至2014年11月13日，刘某等人骗得鲁某某、王某某等10人共计172200元。', '诈骗', '鲁某某、王某某', '刘某', '{\n  \"时间\": \"2014年10月初至2014年11月13日\",\n  \"地点\": \"\",\n  \"人物\": [\"刘某\", \"郭某甲\", \"郭某乙\", \"万某某\", \"鲁某某\", \"王某某\"],\n  \"事件\": [\n    \"被告人刘某与郭某甲、郭某乙、万某某合谋实施诈骗。\",\n    \"郭某甲、郭某乙冒充武警部队工作人员拨打电话，谎称部队需购买产品，并希望对方代购\'自热米饭\'。\",\n    \"刘某告知对方其经销合同已到期，建议对方联系生产商，并提供万某某的电话。\",\n    \"万某某要求对方先付款再发货，并告知对方银行卡账号。\",\n    \"在2014年10月至2014年11月13日期间，刘某等人共计骗得鲁某某、王某某等10人共计172200元。\"\n  ]\n}', '{\n  \"不利信息\": [\n    \"刘某与郭某甲、郭某乙、万某某合谋实施诈骗。\",\n    \"刘某告知对方其经销合同已到期，建议对方联系生产商，并提供万某某的电话。\",\n    \"在2014年10月至2014年11月13日期间，刘某等人共计骗得鲁某某、王某某等10人共计172200元。\"\n  ],\n  \"有利信息\": [\n    \"刘某并未直接冒充武警部队工作人员，而是建议对方联系生产商。\",\n    \"刘某的行为可能被视为未直接参与诈骗的实施。\"\n  ],\n  \"中立信息\": [\n    \"时间为2014年10月初至2014年11月13日。\",\n    \"地点无。\",\n    \"人物包括刘某、郭某甲、郭某乙、万某某、鲁某某、王某某。\"\n  ]\n}', '1.刘某与郭某甲、郭某乙、万某某合谋实施诈骗。\n2.刘某告知对方其经销合同已到期，建议对方联系生产商，并提供万某某的电话。\n3.在2014年10月至2014年11月13日期间，刘某等人共计骗得鲁某某、王某某等10人共计172200元。\n', '1.刘某并未直接冒充武警部队工作人员，而是建议对方联系生产商。\n2.刘某的行为可能被视为未直接参与诈骗的实施。\n', '1.时间为2014年10月初至2014年11月13日。\n2.地点无。\n3.人物包括刘某、郭某甲、郭某乙、万某某、鲁某某、王某某。\n');
